$project19 = Project::create(['name' => ' Initials','open_date' => '2018-07-22 0:00:00','close_date' => '2018-07-24 0:00:00','prompt' => '\n','pre_code' => '','start_code' => 'import turtle\nimport random\nt = turtle.Turtle()\nscreen = turtle.Screen()\ndef randomColor():\n    return (random.randint(0, 255), random.randint(0, 255), random.randint(0, 255))\nt.tracer(0)\nt.speed(1)\nt.up()\nt.hideturtle()\ndef addDot():\n    print("add a dot")\nscreen.onkey(addDot, "space")\nscreen.listen()\n','solution' => 'import turtle\nimport random\nt = turtle.Turtle()\nscreen = turtle.Screen()\ndef randomColor():\n    return (random.randint(0, 255), random.randint(0, 255), random.randint(0, 255))\nt.tracer(0)\nt.speed(1)\nt.up()\nt.hideturtle()\ndots = []\ndef addDot():\n    dots.append(random.randint(-100, 100))\n    for d in dots:\n        t.setpos(d, 0)\n        t.dot(5, randomColor())\n    t.update()\nscreen.onkey(addDot, "space")\nscreen.listen()\naddDot()\n','module_id' => $module14->id,'previous_lesson_id' => $lesson28->id,'user_id' => $user->id]);
